import React, {useState, useEffect} from "react";
import Card from "./Card";
import { key } from "../key";
import {v4 as uuid} from 'uuid';

function MarvelCharacters(props)
{
   const {level, score, highscore, handleClick} = props;
   const [list, setList] = useState([]);
   const [showcase, setShowcase] = useState([]);
    const params = new URLSearchParams({
        ts: key.ts,
        apikey: key.apikey,
        hash: key.hash,
        limit: key.limit, 

    });
    let characters = [];
    let randomList = [];

    const listFunc = async (data) => await 
        setList(data);

    let marvelList = async () => await
    fetch(`https://gateway.marvel.com/v1/public/characters?${params}`, {
        method: "GET",

    }).then( response => {
        return response.json();
    }).then(data => {
        listFunc(data.data.results);
    });
    const shuffleArray = (array) => {
        array.sort((a, b) => 0.5 - Math.random());
    }

    useEffect(() =>
    {
        marvelList();
    }, []);
    useEffect(() =>{
        let shuffle = [...showcase];
        shuffleArray(shuffle);
        setShowcase(shuffle);
    }, [score, highscore])
    useEffect(() =>
    {
        let holder = list.length;
        for(let i = 0; i < 5; i++)
        {
            let random = Math.floor(Math.random() * (holder + 1));
            if(randomList.includes(random))
            {
                while(randomList.includes(random))
                {
                    random = Math.floor(Math.random() * (holder + 1));
                }
                const obj = {key: uuid(), data: list[random]};
                setShowcase([...showcase, obj])
                randomList.push(random);
            }
            else
            {
                const obj = {key: uuid(), data: list[random]};
                setShowcase([...showcase, obj])
                randomList.push(random);
            }

        }

    }, [list]);





    const style = {
        display: "grid",
        width: "100%",
        height: "70vh",
        gridTemplateColumns: `repeat(5, 1fr)`,
        gridTemplateRows: `auto`,
        justifyContent: "center"
    };
    const cardArray = showcase.map((item) => {
        return <Card key={item.key} level={level} score={score} highscore={highscore} data={item.data}/>
    })

    return(
    <div className="Card-Grid" style={style}>
        {cardArray}
    </div>)
}

export default MarvelCharacters;
